mapper.select:
SELECT * FROM goods



            good_name LIKE #{keyword}


            shop_name LIKE #{keyword}



LIMIT #{size} OFFSET #{offset}

SELECT *
FROM goods g
WHERE g.belong_shop = #{shopId}

    AND g.status = #{status}

ORDER BY g.createTime DESC
LIMIT #{offset}, #{size}

SELECT COUNT(1)
FROM goods g
WHERE g.belong_shop = #{shopId}

    AND g.status = #{status}

SELECT COUNT(*) FROM goods



            good_name LIKE #{keyword}


            shop_name LIKE #{keyword}

mapper.delete:
DELETE FROM goods
WHERE id = #{id}
AND belong_shop = #{shopId}

DELETE FROM goods
WHERE belong_shop = #{shopId}
AND id IN

    #{id}
